std::string charset="abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ1234567890";template<typename T>void swap(T &a,T &b){T aux;aux = a;a = b;b = aux;}int random(int min, int max){if(min==max)return min;static bool first = true;if ( first ){srand(time(NULL));first = false;}return min + rand() % (max - min);}void reverse(std::string &a){for(unsigned int i=0;i<a.size()/2;i++)swap(a.at(i),a.at(a.size()-i-1));}int charToNum(char a){switch(a){case '1':return 1;break;case '2':return 2;break;case '3':return 3;break;case '4':return 4;break;case '5':return 5;break;case '6':return 6;break;case '7':return 7;break;case '8':return 8;break;case '9':return 9;break;case '0':return 0;break;default:return -1;break;}}void encrypt1(std::string &a){for(unsigned int i=0; i<a.size(); i++)a.at(i)++;}void decrypt1(std::string &a){for(unsigned int i=0; i<a.size(); i++)a.at(i)--;}void encrypt2(std::string &a){reverse(a);}void decrypt2(std::string &a){reverse(a);}void encrypt3(std::string &a){for(unsigned int i=0; i<a.size()-2; i+=2)swap(a.at(i),a.at(i+1));}void decrypt3(std::string &a){for(unsigned int i=0; i<a.size()-2; i+=2)swap(a.at(i),a.at(i+1));}void encrypt4(std::string &a,const std::string &key){for(unsigned int i=0; i<a.size(); i++)a.at(i)+=key.at(i%key.length());}void decrypt4(std::string &a,const std::string &key){for(unsigned int i=0; i<a.size(); i++)a.at(i)-=key.at(i%key.length());}void encrypt5(std::string &a){for(unsigned int i=0; i<a.size(); i++)a.at(i)+=i;}void decrypt5(std::string &a){for(unsigned int i=0; i<a.size(); i++)a.at(i)-=i;}void encrypt6(std::string &a){std::string b;for(unsigned int i=0; i<a.size(); i++){b.push_back(a.at(i));b.push_back(charset.at(random(0,charset.size()-1)));}a=b;}void decrypt6(std::string &a){std::string b;for(unsigned int i=0; i<a.size(); i+=2)b.push_back(a.at(i));a=b;}void doEncrypt(std::string &a,const std::string &key){for(auto&zz:key){switch(zz){case '1':encrypt1(a);break;case '2':encrypt2(a);break;case '3':encrypt3(a);break;case '4':encrypt4(a,key);break;case '5':encrypt5(a);break;case '6':encrypt6(a);break;}}}void doDecrypt(std::string &a,const std::string &key){std::string key1 = key;encrypt2(key1);for(auto&zz:key1){switch(zz){case '1':decrypt1(a);break;case '2':decrypt2(a);break;case '3':decrypt3(a);break;case '4':decrypt4(a,key);break;case '5':decrypt5(a);break;case '6':decrypt6(a);break;}}}
